import {useState, useEffect} from 'react';

import axios from "axios";

export const useName = () => {


	const [name, setName] = useState([])

	const getNames = async () => {
		axios.get('https://api.fungenerators.com/name/generate?category=car&limit=100')
			.then(res => {
				const katNames = res.data.contents.names
				setName(katNames)
		})
	}

	useEffect(() => {
		getNames();
	}, [])
	return {name}
}
