import {useState, useEffect} from 'react';

import axios from "axios";

export const useName = (kats) => {


	const [names, setName] = useState([])

	const getNames = async () => {
		await axios.get('https://api.fungenerators.com/name/generate?category=car&limit=100')
			.then(res => {
				const katNames = res.data.contents.names
				katNames.map(name => {
					Object.assign(kats, {
						cat: name
					})
				})
				setName(katNames)
		})
	}

	useEffect(() => {
		getNames();
	}, [])
	return {names}
}
