import React, {useState, useEffect} from 'react'
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { v4 as uuidv4 } from 'uuid';

import axios from "axios";

import { useSelector } from "react-redux";

import KatsItem from '../kats-item/kats-item.component'

import { useName } from '../../hooks/useName.jsx'

import { Button, View, StyleSheet, Text } from 'react-native';

import {uploadCats} from '../../redux/kats/kats.actions';
import { filterCats } from '../../redux/kats/kats.actions'
import { selectKatsList } from '../../redux/kats/kats.selectors'


const styles = StyleSheet.create({
  container: {
    paddingTop: 0,
    height:600,
  }, individual: {

  },
});


function KatsOverview({uploadCats, katImages, filterCats}) {

  const [kats, setKats] = useState([])

  const katsImages = useSelector(state => state.kats.katsList)
  const katsCount = useSelector(state => state.kats.count)

  useEffect(() => {
  getCats();

}, [])

  useEffect(() => {
    let result = [...katsImages];

      if (katsCount) {
      console.log(result.filter(kat => kat.length < katsCount))
      }
      //setKats(result);

  }, [katsImages, katsCount])


  //getting names
  const { names } = useName(kats);

  const getCats = async () => {

  // getting cats
  let catMap = [];
  let maxItter = 100;

  //random images
  let max = 16
  let value = 0
  let up = true

  //assigning name
  let nameCount = 0

  do {
    try {
  await axios.get(`http://placekitten.com/200/300?image=${value}`, {
    responseType: 'blob'
  })
   .then(res => {
      let cat = Object.assign(kats, {
                  preview: res.config.url,
                  id: uuidv4(),
                  name: names[0]
              }
        )
      catMap.push(cat)
     })
      maxItter--
      nameCount++

      if (up == true && value <= max) {
       value++

      if (value == max) {
        up = false;
      }
    }  else {
        up = false
        value--;

      if (value == 0) {
        up = true;
        }
      }

    } catch (error) {
      console.log(error)
    }
  }
  while (maxItter > 0);
  setTimeout(function() {
      console.log('aa')
    }, 2000);
  uploadCats(catMap);
}





	return (
		<View style={styles.container}>
    {console.log(kats)}
      <View style={{flexDirection:'row', flexWrap:'wrap', height: 150, margin: 20, width: 100}}>
          <Button
            onPress={() => filterCats(30)}
            title="30"
          />
          <Button
            onPress={() => filterCats(50)}
            title="50"
          />
          <Button
            onPress={() => filterCats(100)}
            title="100"
          />
        </View>
        { 
        katsImages.map((item, index) => (
          <KatsItem
              key={item.id}
              id={index}
              names={names}
              preview={item.preview}
            />
          ))
        }
    	</View>
		)
}

const mapDispatchToProps = dispatch => ({
  uploadCats: (catArr) => dispatch(uploadCats(catArr)),
  filterCats: (count) => dispatch(filterCats(count))
})

const mapStateToProps = (state) => ({
  katImages: selectKatsList(state)
})

export default connect(null, mapDispatchToProps)(KatsOverview);
