import {useState, useEffect} from 'react';
import { useDispatch } from "react-redux";

import axios from "axios";

import loadingCats from '../redux/kats/kats.actions'

export const useName = (kats) => {

	const [names, setName] = useState([])

	const CancelToken = axios.CancelToken;
  	const source = CancelToken.source();

  	const dispatch = useDispatch();

	const getNames = async () => {
		try {
			await axios.get('https://api.fungenerators.com/name/generate?category=car&limit=100',
			{ cancelToken: source.token })
			.then(res => {
				dispatch(loadingCats())
				const katNames = res.data.contents.names
				katNames.map(name => {
					kats.push({cat: name}) 
				})
				setName(katNames)
		})
		} catch (error) {
			throw error
		}
	}

	useEffect(() => {
		getNames();
			return () => {
	      source.cancel();
    };
	}, [])
	return {names}
}
