import React, {useState, useEffect} from 'react'
import { 
  Button, 
  View, 
  StyleSheet, 
  Text, 
  FlatList, 
  TouchableOpacity, 
  TextInput 
} from 'react-native';

import { connect } from 'react-redux';

import { useSelector, useDispatch } from "react-redux";
import 'react-native-get-random-values'
import { v4 as uuidv4 } from 'uuid';
import axios from "axios";

import KatsItem from '../kats-item/kats-item.component'

import {requestCats} from '../../redux/kats/kats.actions';
import { filterCats } from '../../redux/kats/kats.actions'
import { loadingCats } from '../../redux/kats/kats.actions'

import Spinner from 'react-native-loading-spinner-overlay';
import Modal from 'react-native-modal';


const styles = StyleSheet.create({
  container: {
    paddingTop: 50,
    height:750,
    marginTop: 0
  }, spinnerTextStyle: {
    color: 'black'
  }
});

function KatsOverview({uploadCats, filterCats, navigation}) {


  const [katFilter, setKatFilter] = useState([])

  const [text, onChangeText] = useState(undefined);

  const [isModalVisible, setModalVisible] = useState(false);


  const katsImages = useSelector(state => state.kats.katsList)
  const katsCount = useSelector(state => state.kats.count)
  const isLoading = useSelector(state => state.kats.isUploading)
  const imagesIn = useSelector(state => state.kats.imagesDone)

  const dispatch = useDispatch();


  useEffect(() => {
    let result = [...katsImages];

      if (katsCount) {
      result = result.slice(0, katsCount)
      }
      setKatFilter(result);

  }, [katsImages, katsCount])

useEffect(() => {
  dispatch(requestCats());
}, [])

const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };


	return (
		<View style={{flex:1}}>
      <Spinner
          visible={isLoading}
          color={'black'}
          textContent={'Loading...'}
          style={styles.spinnerTextStyle}
        />
        <TouchableOpacity 
          style={{width:'20%', backgroundColor:'orange', marginHorizontal:30, borderRadius: 5, justifyContent: 'center', alignSelf:'center', marginTop: 20}}
          onPress={toggleModal}>
          <Text style={{color:'white', alignSelf:'center', padding:5}}>Filter</Text>
        </TouchableOpacity>

        <Modal isVisible={isModalVisible}>
          <View style={{flex:1, flexDirection:'row', justifyContent: 'center', alignItems: 'center', marginTop:100}}>     
               <TouchableOpacity
                    onPress={() => filterCats(30)}
                    style={{width:'25%', borderColor:'white', height: '20%', borderWidth:1, marginHorizontal:25, borderRadius: 12,}}
                  >
                    <Text style={{color:'white', alignSelf:'center', padding:0, fontSize: 30,}}>30</Text>
                </TouchableOpacity>
                  <TouchableOpacity
                  onPress={() => filterCats(50)}
                   style={{width:'25%', height: '20%', borderWidth:1, borderColor:'white', marginHorizontal:25, borderRadius: 12,}}
                    >
                   <Text style={{color:'white', alignSelf:'center', padding:0, fontSize: 30,}}>50</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    onPress={() => filterCats(100)}
                   style={{width:'25%', height: '20%', borderColor:'white', borderWidth:1, marginHorizontal:25, borderRadius: 12,}}
                    >
                   <Text style={{color:'white', alignSelf:'center', padding:0, fontSize: 30,}}>100</Text>
                  </TouchableOpacity>
                  </View>
                  <View style={{flex: 1,
                    justifyContent: 'center',
                    alignItems: 'center',
                    marginBottom:150,
                  }}>
                  <View style={{flex: 1,}}>
                    <TextInput
                      placeholder='type number'
                      placeholderTextColor="white"
                      style={{height: 40, width:150, borderColor: 'gray', borderWidth: 1, color: 'white'}}
                      onChangeText={(text) => {
                        onChangeText(text);
                        filterCats(text)
                      }}
                      value={text}
                      defaultValue={text}
                    />
                  </View>
                    </View>
                  <TouchableOpacity 
                  style={{width:'30%', height: 32, alignSelf:'center', backgroundColor:'orange', marginHorizontal:30, borderRadius: 5,}}
                  onPress={toggleModal}>
                    <Text style={{color:'white', alignSelf:'center', padding:7}}>Close</Text>
                  </TouchableOpacity>
          </Modal>
             <View style={styles.container}>
              <FlatList
                style={{flex:1}}
                data={katFilter}
                renderItem={({item}) => <KatsItem key={item.id} item={item} navigation={navigation}/>}
                keyExtractor={item => item.id}
              />
          	</View>
      </View>
		)
}

const mapDispatchToProps = dispatch => ({
  uploadCats: (catArr) => dispatch(uploadCats(catArr)),
  filterCats: (count) => dispatch(filterCats(count))
})

export default connect(null, mapDispatchToProps)(KatsOverview);
