import React, {useEffect} from 'react'
import { connect } from 'react-redux';

import { StyleSheet, Text, View } from 'react-native';

import {uploadCats} from '../../redux/kats/kats.actions'


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});


function KatsOverview({uploadCats}) {

  const getCats = () => {

  let catMap = [];
  let maxItter = 20;

  do {
    try {

  axios.get('http://placekitten.com/200/300', {
    responseType: 'blob'
  })
   .then(res => {
      let cat = Object.assign(res.data, {
                  preview: URL.createObjectURL(res.data),
                  id: uuidv4()
              }
        )
      catMap.push(cat)
     })
      maxItter--
    } catch (error) {
      console.log(error)
    }
  }
  while (maxItter > 0);
  uploadCats(catMap)
}

useEffect(() => {
  getCats();

}, [])

	return (
		<View style={styles.container}>
	      <Text>Open up !</Text>
    	</View>
		)
}

const mapDispatchToProps = dispatch => ({
  uploadCats: (catArr) => dispatch(uploadCats(catArr))
})

export default connect(null, mapDispatchToProps)(KatsOverview);
