import React, {useState, useEffect} from 'react'
import { connect } from 'react-redux';

import { v4 as uuidv4 } from 'uuid';

import axios from "axios";

import { useSelector } from "react-redux";

import { StyleSheet, Text, View, Image } from 'react-native';

import {uploadCats} from '../../redux/kats/kats.actions';


const styles = StyleSheet.create({
  container: {
    paddingTop: 50,
  },
  tinyLogo: {
    width: 50,
    height: 50,
  },
  logo: {
    width: 66,
    height: 58,
  },
});


function KatsOverview({uploadCats}) {

  const [kat, setKat] = useState([])

  const katImages = useSelector(state => state.kats.katsList)

  const getCats = () => {

  let catMap = [];
  let maxItter = 20;

  do {
    try {

  axios.get('http://placekitten.com/200/300', {
    responseType: 'blob'
  })
   .then(res => {
      let cat = Object.assign(res, {
                  preview: URL.createObjectURL(res.data),
                  id: uuidv4()
              }
        )
      catMap.push(cat)
      setKat(res.config.url)
     })
      maxItter--
    } catch (error) {
      console.log(error)
    }
  }
  while (maxItter > 0);
  uploadCats(catMap)
}

useEffect(() => {
  getCats();

}, [])

	return (
		<View style={styles.container}>
{
  katImages.map(({id, preview}) => 
     <Image
        source={{
          width: 100,
          height: 100,
          uri: preview
        }} 
        />
    )
}


    	</View>
		)
}

const mapDispatchToProps = dispatch => ({
  uploadCats: (catArr) => dispatch(uploadCats(catArr))
})

export default connect(null, mapDispatchToProps)(KatsOverview);
