import katsActionTypes from './kats.types';

const INITIAL_STATE = {
  katsList: [],
  isUploading: false
};

const katsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case katsActionTypes.UPLOAD_KATS:
      return {
        ...state,
        katsList: action.payload
      };
    case katsActionTypes.FILTER_CATS:
      return {
      ...state,
      count: action.payload
    }
    case katsActionTypes.LOADING_CATS:
      return {
      ...state,
      isUploading: !state.isUploading
    }
    default:
      return state;
  }
};

export default katsReducer;
