import {useEffect, useState} from 'react';
import { useDispatch } from "react-redux";

import axios from "axios";

import {loadingCats} from '../redux/kats/kats.actions'

export const useName = (kats) => {

	const [names, setName] = useState([])

  	const dispatch = useDispatch();

  	const CancelToken = axios.CancelToken;
  	const source = CancelToken.source();

	const getNames = async () => {

		const arr = []

		try {
			await axios.get('https://bikeindex.org/api/v3/manufacturers?page=1&per_page=100', {
		    cancelToken: source.token
		  	})
			.then(res => {
				let catnames = Object.values(res.data)
							.map(res => res
								.map(kitty => kitty.name))
					let unnestedNames = catnames.flat()
					let spread = [...unnestedNames]
					spread.map(item => {
						arr.push({cat: item})
						setName(arr)
					})
				})
				.catch((err) => {
			console.log('limited api requests(5 a day)')
			console.log(err)
		})
		} catch (error) {
			throw error
		}
	}

	useEffect(() => {
		getNames();
		dispatch(loadingCats());
		setTimeout(() => {
	    console.log('aa')
	  }, 1500);
			return () => {
	      	source.cancel();
    };
	}, [])
	return {names}
}
