  	const source = CancelToken.source();
//5cb4753b6e42bab3a4859d6b3e6d911b
	const getNames = async () => {
		try {
			await axios.get('https://api.parser.name/?api_key=5cb4753b6e42bab3a4859d6b3e6d911b&endpoint=generate&results=3', {
    cancelToken: source.token
  })
			.then(res => {
				console.log(res)
