				// })
		})
			.catch((err) => {
			console.log('limited api requests(5)')
			console.log(err)
			})
		} catch (error) {
			throw error
		}
	}

	useEffect(() => {
		dispatch(loadingCats());
		getNames();
			return () => {
	      	source.cancel();
    };
	}, [dispatch])
}
