import {useEffect} from 'react';
import { useDispatch } from "react-redux";

import axios from "axios";

import {loadingCats} from '../redux/kats/kats.actions'

export const useName = (kats) => {

  	const dispatch = useDispatch();

  	const CancelToken = axios.CancelToken;
  	const source = CancelToken.source();
//https://api.fungenerators.com/name/generate?category=car&limit=100
	const getNames = async () => {
		try {
			await axios.get('https://bikeindex.org/api/v3/manufacturers?page=0&per_page=100', {
    cancelToken: source.token
  })
			.then(res => {
				let manufacturerNames = Object.values(res.data)
					.map(i => i
						.map(manufacturer => {
							kats.push({cat: manufacturer.name})}))
				})
				.catch((err) => {
					console.log('limited api requests(5 a day)')
					console.log(err)
				// const katNames = res.data.contents.names
				// katNames.map(name => {
				// 	kats.push({cat: name}) 
		})

		} catch (error) {
			throw error
		}
	}

	useEffect(() => {
		dispatch(loadingCats());
		getNames();
			return () => {
	      	source.cancel();
    };
	}, [])
}
