import {useState, useEffect} from 'react';
import { useDispatch } from "react-redux";

import axios from "axios";

import {loadingCats} from '../redux/kats/kats.actions'



export const useName = (kats) => {

	const [names, setName] = useState([])

  	const dispatch = useDispatch();

  	const CancelToken = axios.CancelToken;
  	const source = CancelToken.source();

	const getNames = async () => {
		try {
			await axios.get('http://names.drycodes.com/100', {
    cancelToken: source.token
  })
			.then(res => {
				console.log(res)
		// 		const katNames = res.data.contents.names
		// 		katNames.map(name => {
		// 			kats.push({cat: name}) 
		// 		})
		 })
			.catch((err) => {
			console.log('limited api requests(5)')
			console.log(err)
			})
		} catch (error) {
			throw error
		}
	}

	useEffect(() => {
		dispatch(loadingCats());
		getNames();
			return () => {
	      	source.cancel();
    };
	}, [dispatch])
	return {names}
}
