import React, {useState, useEffect} from 'react'
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { v4 as uuidv4 } from 'uuid';
import 'react-native-get-random-values'

import axios from "axios";

import { useSelector } from "react-redux";

import KatsItem from '../kats-item/kats-item.component'

import { useName } from '../../hooks/useName.jsx'

import { Button, View, StyleSheet, Text, FlatList } from 'react-native';

import {uploadCats} from '../../redux/kats/kats.actions';
import { filterCats } from '../../redux/kats/kats.actions'
import { selectKatsList } from '../../redux/kats/kats.selectors'


const styles = StyleSheet.create({
  container: {
    paddingTop: 0,
    height:600,
  }, individual: {

  },
});


function KatsOverview({uploadCats, katImages, filterCats}) {

  const [kats, setKats] = useState([])

  const [katFilter, setKatFilter] = useState([])

  const CancelToken = axios.CancelToken;
  const source = CancelToken.source();

  const katsImages = useSelector(state => state.kats.katsList)
  const katsCount = useSelector(state => state.kats.count)

  useEffect(() => {
  getCats();
  return () => {
      source.cancel();
    }
}, [])

  useEffect(() => {
    let result = [...katsImages];

      if (katsCount) {
      result = result.slice(0, katsCount)
      }
      setKatFilter(result);

  }, [katsImages, katsCount])


  //getting names
  const { names } = useName(kats);

  const getCats = async () => {

  // getting cats
  let catMap = [];
  let maxItter = 100;

  //random images
  let max = 16
  let value = 0
  let up = true

  do {
    try {

  await axios.get(`http://placekitten.com/200/300?image=${value}`, {
    responseType: 'blob',
    cancelToken: source.token
  })
   .then(res => {
      let cat = Object.assign({}, {
                  preview: res.config.url,
                  id: uuidv4(),
              }
        )
      catMap.push(cat)
     })
      maxItter--

      if (up == true && value <= max) {
       value++

      if (value == max) {
        up = false;
      }
    }  else {
        up = false
        value--;

      if (value == 0) {
        up = true;
        }
      }

    } catch (error) {
      console.log(error)
      throw error;
    }
  }
  while (maxItter > 0);

  const obj = kats.map((item, i) => Object.assign({}, item, catMap[i]))

  setTimeout(function() {
      console.log('aa')
    }, 2000);
  uploadCats(obj);
}


	return (
		<View>
      <View style={{flexDirection:'row', flexWrap:'wrap', height: 150, margin: 20, width: 100}}>
          <Button
            onPress={() => filterCats(30)}
            title="30"
          />
          <Button
            onPress={() => filterCats(50)}
            title="50"
          />
          <Button
            onPress={() => filterCats(100)}
            title="100"
          />
      </View>
       <View style={styles.container}>
        <FlatList
          style={{flex:1}}
          data={katFilter}
          renderItem={({item }) => <KatsItem item={item}/>}
          keyExtractor={item => item.id}
        />
    	</View>
      </View>
		)
}

const mapDispatchToProps = dispatch => ({
  uploadCats: (catArr) => dispatch(uploadCats(catArr)),
  filterCats: (count) => dispatch(filterCats(count))
})

const mapStateToProps = (state) => ({
  katImages: selectKatsList(state)
})

export default connect(null, mapDispatchToProps)(KatsOverview);
