import React, {useState, useEffect} from 'react'
import { 
  Button, 
  View, 
  StyleSheet, 
  Text, 
  FlatList, 
  TouchableOpacity, 
  TextInput 
} from 'react-native';

import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { useSelector, useDispatch } from "react-redux";
import 'react-native-get-random-values'
import { v4 as uuidv4 } from 'uuid';
import axios from "axios";

import KatsItem from '../kats-item/kats-item.component'
import { useName } from '../../hooks/useName.jsx'

import {uploadCats} from '../../redux/kats/kats.actions';
import { filterCats } from '../../redux/kats/kats.actions'
import { loadingCats } from '../../redux/kats/kats.actions'

import Spinner from 'react-native-loading-spinner-overlay';


const styles = StyleSheet.create({
  container: {
    paddingTop: 50,
    height:650,
  }, spinnerTextStyle: {
    color: 'black'
  }
});

function KatsOverview({uploadCats, filterCats, navigation}) {

  const [kats, setKats] = useState([])

  const [katFilter, setKatFilter] = useState([])

  const [value, onChangeText] = useState('Useless Placeholder');


  const katsImages = useSelector(state => state.kats.katsList)
  const katsCount = useSelector(state => state.kats.count)
  const isLoading = useSelector(state => state.kats.isUploading)

  const dispatch = useDispatch();

  const CancelToken = axios.CancelToken;
  const source = CancelToken.source();

  useEffect(() => {
  getCats();
  return () => {
      source.cancel();
    }
}, [])

  useEffect(() => {
    let result = [...katsImages];

      if (katsCount) {
      result = result.slice(0, katsCount)
      }
      setKatFilter(result);

  }, [katsImages, katsCount])


  //getting names
  const { names } = useName(kats);

  const getCats = async () => {

  // getting cats
  let catMap = [];
  let maxItter = 100;

  //random images
  let max = 16
  let value = 0
  let up = true

  do {
    try {

  await axios.get(`http://placekitten.com/200/300?image=${value}`, {
    cancelToken: source.token
  })
   .then(res => {
      let cat = Object.assign({}, {
                  preview: res.config.url,
                  id: uuidv4(),
              }
        )
      catMap.push(cat)
     })
      maxItter--

      if (up == true && value <= max) {
       value++

      if (value == max) {
        up = false;
      }
    }  else {
        up = false
        value--;

      if (value == 0) {
        up = true;
        }
      }

    } catch (error) {
      console.log(error)
      throw error;
    }
  }
  while (maxItter > 0);

  const obj = kats.map((item, i) => Object.assign({}, item, catMap[i]))

  uploadCats(obj);
  dispatch(loadingCats());
    setTimeout(() => {
    console.log('aa')
  }, 100);
}


	return (
		<View style={{flex:1}}>
      <Spinner
          visible={isLoading}
          color={'black'}
          textContent={'Loading...'}
          style={styles.spinnerTextStyle}
        />
      <View style={{flex:1, height: 100, flexDirection:'row', alignItems:'flex-end', justifyContent: 'center', paddingBottom: 0}}>     
           <TouchableOpacity
                onPress={() => filterCats(30)}
                style={{width:'20%', backgroundColor:'orange', marginHorizontal:30, borderRadius: 5,}}
              >
                <Text style={{color:'white', alignSelf:'center', padding:5}}>30</Text>
            </TouchableOpacity>
              <TouchableOpacity
              onPress={() => filterCats(50)}
               style={{width:'20%', backgroundColor:'orange',marginHorizontal:30, borderRadius: 5,}}
                >
               <Text style={{color:'white',alignSelf:'center', padding:5}}>50</Text>
              </TouchableOpacity>
              <TouchableOpacity
              onPress={() => filterCats(100)}
               style={{width:'20%', backgroundColor:'orange',marginHorizontal:30, borderRadius: 5,}}
                >
               <Text style={{color:'white', alignSelf:'center', padding:5}}>100</Text>
              </TouchableOpacity>
              </View>

              <TextInput
                style={{ justifyContent: 'center',alignSelf:'center', height: 40, width:150, padding:20, borderColor: 'gray', borderWidth: 1, marginTop: 10}}
                onChangeText={text => onChangeText(text)}
                value={value}
              />
       <View style={styles.container}>
        <FlatList
          style={{flex:1}}
          data={katFilter}
          renderItem={({item}) => <KatsItem item={item} navigation={navigation}/>}
          keyExtractor={item => item.id}
        />
    	</View>
      </View>
		)
}

const mapDispatchToProps = dispatch => ({
  uploadCats: (catArr) => dispatch(uploadCats(catArr)),
  filterCats: (count) => dispatch(filterCats(count))
})

export default connect(null, mapDispatchToProps)(KatsOverview);
