import React, {useState, useEffect} from 'react'
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { selectKatsList } from '../../redux/kats/kats.selectors'

import { v4 as uuidv4 } from 'uuid';

import axios from "axios";

import { useSelector } from "react-redux";

import { StyleSheet, Text, View, Image } from 'react-native';

import {uploadCats} from '../../redux/kats/kats.actions';


const styles = StyleSheet.create({
  container: {
    paddingTop: 50,
    height:600,
  },
  tinyLogo: {
    width: 50,
    height: 50,
  },
  logo: {
    width: 66,
    height: 58,
  },
});


function KatsOverview({uploadCats, katImages}) {

  const [kat, setKat] = useState(false)

  useEffect(() => {
  getCats();

}, [])

  const getCats = async () => {

  let catMap = [];
  let maxItter = 20;

  do {
    try {

  await axios.get('http://placekitten.com/200/300', {
    responseType: 'blob'
  })
   .then(res => {
      let cat = Object.assign({}, {
                  preview: res.config.url,
                  id: uuidv4()
              }
        )
      catMap.push(cat)
     })
      maxItter--
    } catch (error) {
      console.log(error)
    }
  }
  while (maxItter > 0);
  uploadCats(catMap);
}

const katsImg = useSelector(state => state.kats.katsList)

	return (
		<View style={styles.container}>
    {console.log(katsImg.map(img => img.preview))}
    {
      kat === true ?
      <View>
        { 
        katsImg.map(({id, preview}) => (
          <Image
              key={id}
              source={{
                width: 100,
                height: 100,
                uri: preview}}
              /> 
          ))

} </View>
: null
  }
    	</View>
		)
}

const mapDispatchToProps = dispatch => ({
  uploadCats: (catArr) => dispatch(uploadCats(catArr))
})

const mapStateToProps = createStructuredSelector({
  katImages: selectKatsList,
})

export default connect(mapStateToProps, mapDispatchToProps)(KatsOverview);
